INSERT INTO `lv_articles` VALUES ('29', '媒体：中国开始屏蔽外国VPN服务', '李四', '媒体：中国开始屏蔽外国VPN服务', '<p><img src=\"http://localhost:8000/upload/image/20150123/1422012888457255.jpg\" alt=\"1422012888457255.jpg\"/></p><p><br/></p><p>【中国屏蔽外国VPN服务！】《环球时报》英文网报道，中国已开始屏蔽外国VPN服务。VPN供应商Astrill通知用户，因防火长城升级，使用\r\nIPSec、L2TP/IPSec和PPTP协议的设备无法访问它的服务，受影响的主要是iOS设备。中国工信部曾规定，在中国提供VPN服务的公司必须\r\n登记注册，否则将“不受中国法律的保护”</p>', '2015-01-23 19:36:11', '2015-01-23 19:36:11', '3', '8', '1');

-- ----------------------------
-- Table structure for `lv_badmin`
-- ----------------------------
DROP TABLE IF EXISTS `lv_badmin`;
CREATE TABLE `lv_badmin` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `nickname` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `username` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `password` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `updated_at` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`id`),
